import * as React from "react";
import Switch from "@mui/material/Switch";
import Checkbox from "@mui/material/Checkbox";
import TextField from "@mui/material/TextField";
import Autocomplete from "@mui/material/Autocomplete";
import CheckBoxOutlineBlankIcon from "@mui/icons-material/CheckBoxOutlineBlank";
import CheckBoxIcon from "@mui/icons-material/CheckBox";
import FormControlLabel from "@mui/material/FormControlLabel";
import Popper from "@mui/material/Popper";

const icon = <CheckBoxOutlineBlankIcon fontSize="small" />;
const checkedIcon = <CheckBoxIcon fontSize="small" />;

// Custom Popper component that allows setting the placement dynamically
const CustomPopper = (props) => {
  return <Popper {...props} placement={props.placement} />;
};

export default function CheckboxesTags() {
  const [searchEnabled, setSearchEnabled] = React.useState(true);
  const [dropdownDirectionUpwards, setDropdownDirectionUpwards] =
    React.useState(false);

  const handleSearchToggle = (event) => {
    setSearchEnabled(event.target.checked);
  };

  const handleDropdownDirectionToggle = (event) => {
    setDropdownDirectionUpwards(event.target.checked);
  };

  return (
    <div style={{ display: "flex", alignItems: "center", gap: "20px" }}>
      <Autocomplete
        multiple
        id="checkboxes-tags-demo"
        options={top100Films}
        disableCloseOnSelect
        getOptionLabel={(option) => option.title}
        renderOption={(props, option, { selected }) => (
          <li {...props}>
            <Checkbox
              icon={icon}
              checkedIcon={checkedIcon}
              style={{ marginRight: 8 }}
              checked={selected}
            />
            {option.title}
          </li>
        )}
        style={{ width: 500 }}
        renderInput={(params) => (
          <TextField
            {...params}
            label=""
            placeholder="Search..."
            disabled={!searchEnabled}
          />
        )}
        PopperComponent={(props) => (
          <CustomPopper
            {...props}
            placement={dropdownDirectionUpwards ? "top" : "bottom"}
          />
        )}
      />

      <FormControlLabel
        control={
          <Switch checked={searchEnabled} onChange={handleSearchToggle} />
        }
        label="Enable Search"
      />
      <FormControlLabel
        control={
          <Switch
            checked={dropdownDirectionUpwards}
            onChange={handleDropdownDirectionToggle}
          />
        }
        label="Dropdown Opens Upwards"
      />
    </div>
  );
}

// Top 100 films as rated by IMDb users. http://www.imdb.com/chart/top
const top100Films = [
  { title: "The Shawshank Redemption", year: 1994 },
  { title: "The Godfather", year: 1972 },
  { title: "The Godfather: Part II", year: 1974 },
  { title: "The Dark Knight", year: 2008 },
