import * as React from "react";
import { useState } from "react";
import TextField from "@mui/material/TextField";
import Autocomplete from "@mui/material/Autocomplete";
import { createTheme, ThemeProvider } from "@mui/material/styles";
import { CssBaseline, Switch } from "@mui/material";

const top100Films = [
  // Your array items here
];

export default function Home({ options, showCheckmark }) {
  const [darkMode, setDarkMode] = useState(false);

  const theme = React.useMemo(
    () =>
      createTheme({
        palette: {
          mode: darkMode ? "dark" : "light",
        },
      }),
    [darkMode]
  );

  const handleThemeChange = (event) => {
    setDarkMode(event.target.checked);
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Switch checked={darkMode} onChange={handleThemeChange} /> Toggle Dark
      Mode
      {/* <Autocomplete
        disablePortal
        id="combo-box-demo"
        options={top100Films}
        sx={{ width: 300 }}
        renderInput={(params) => <TextField {...params} label="Movie" />}
      /> */}
      <Autocomplete
        disablePortal
        id="combo-box-demo"
        options={options}
        sx={{ width: 300 }}
        getOptionLabel={(option) => option.label} // Specifies how to extract the label from each option
        renderOption={(props, option, { selected }) => (
          <li {...props}>
            {showCheckmark && selected ? <CheckIcon fontSize="small" /> : null}{" "}
            {/* Display the checkmark if showCheckmark is true and the option is selected */}
            {option.label}
          </li>
        )}
        renderInput={(params) => <TextField {...params} label="Movie" />}
      />
    </ThemeProvider>
  );
}
